baseCSS:
/* 标题色 */
.note-to-mp h1,
.note-to-mp h2,
.note-to-mp h3,
.note-to-mp h4,
.note-to-mp h5,
.note-to-mp h6 {
    color: #FFB347;
}
/* 加粗文字 */
.note-to-mp strong {
    color: #FFB347;
}
/* 斜体文字 */
.note-to-mp em {
    color: #33357D;
}
/* 分割线 */
.note-to-mp hr {
    border-top: 1px solid #FFB347;
}
